import React from "react";
import Box from "@mui/material/Box";
// import SwipeableViews from "react-swipeable-views";
import { AllProjects } from "./ProjectInfo";
import { Link } from "@mui/material";
import GitHubIcon from "@mui/icons-material/GitHub";
import LinkIcon from "@mui/icons-material/Link";
import "./Project.css";
import { Carousel } from "react-bootstrap";
// import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
// import { Carousel } from 'react-responsive-carousel';

// const AutoPlaySwipeableViews = autoPlay(SwipeableViews);

const MinorProjectCarousel = () => {

  return (
    <>

      <div>
        <Carousel > 
          {AllProjects.map((Project) => (
            Project.category==='minor'&&

              // <div
              //   style={{
              //     display: "flex",
              //     flexDirection: "column",
              //     alignItems: "center",
              //   }}
              // >

                  <>
                <Carousel.Item key={Project.c_id}>
                    <div className="card_1">
                      <div className="card-image">
                        <img
                          src={require("../../assets/projects/" +
                          Project.image +
                          ".png")}
                          alt="profile one"
                          height="100%"
                          width="100%"
                          />
                      </div>
                          <Carousel.Caption>
                          <div className="design_1" style={{backgroundColor:`${Project.tag_colour}`}}></div>
                      <ul className="social-icons">
                        <li>
                          <Link href={Project.github_link} target="_blank">
                            <GitHubIcon />
                          </Link>
                        </li>
                        <li>
                          {
                            // console.log(Project.site_link)
                            Project.site_link === "" ? (
                              <Link href="">
                                <LinkIcon />
                              </Link>
                            ) : (
                              <Link target="_blank" href={Project.site_link}>
                                <LinkIcon />
                              </Link>
                            )
                          }
                        </li>
                      </ul>
                    </Carousel.Caption>

                    </div>
                    {/* <div className="details">
                      <h3>{Project.title}</h3>
                    </div> */}
              </Carousel.Item>
                  </>
              // </div>


          ))}
          </Carousel>



      </div>
    </>
  );
};

export default MinorProjectCarousel;
